import React, { Component, useState } from 'react';
import './App.css';
import {BrowserRouter, Switch, Route} from "react-router-dom";
import ReactMapGL from 'react-map-gl';
import SearchBox from "@seanhouli/react-mapbox-search";
import Geocoder from 'react-mapbox-gl-geocoder';
import 'bootstrap/dist/css/bootstrap.min.css'

import {Dropdown, FormControl, } from "react-bootstrap";

const MAPBOX_TOKEN = "pk.eyJ1IjoiZWxpamFoYWhtYWQiLCJhIjoiY2tnZ2tqdDkyMDI5bDMwcGdhcDAxNXFyNiJ9.3sIoybXDm1A_gILEb1b9CA";

const params = {
    country: "ca"
}




class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            information: {},
            viewport: {
                width: window.innerWidth,
                height: window.innerHeight,
                latitude: 37.7577,
                longitude: -122.4376,
                zoom: 8
            },
            value: ''
        }
    }

    componentWillMount() {
        fetch('/yesterday/uk').then(res => res.json()).then(data => {
            this.setState({information: data[0]});
            console.log(this.state.information);
        });
    }

    onSelected = (viewport, item) => {
        this.setState({
            viewport
        })
    }




    /*
    <ReactMapGL
                        style={{
                            position: "fixed",
                            width: window.innerWidth,
                            height: window.innerHeight,
                            backgroundSize: "cover",
                            zIndex: -1,
                        }}
                        width={viewport.width}
                        height={viewport.height}
                        latitude={viewport.latitude}
                        longitude={viewport.longitude}
                        zoom={viewport.zoom}
                        mapStyle="mapbox://styles/elijahahmad/ckggklsqg3atg1aokg07dhotu"
                        onViewportChange={(viewport) => this.setState({viewport})}
                        mapboxApiAccessToken={MAPBOX_TOKEN}
                    >
                        <Geocoder
                            mapboxApiAccessToken={MAPBOX_TOKEN}
                            onSelected={this.onSelected}
                            viewport={viewport}
                            hideOnSelect={true}
                            value=""
                            queryParams={params}
                        />

                        <p>The Current Information for UK - {this.state.information["Date"]}</p>
                        <p>Active Cases: {this.state.information["Active"]}</p>
                        <p>Confirmed Cases: {this.state.information["Confirmed"]}</p>
                        <p>Current Deaths: {this.state.information["Deaths"]}</p>

                    </ReactMapGL>
     */

    CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
        <a
            href=""
            ref={ref}
            onClick={(e) => {
                e.preventDefault();
                onClick(e);
            }}
        >
            {children}
            &#x25bc;
        </a>
    ));

// forwardRef again here!
// Dropdown needs access to the DOM of the Menu to measure it
    CustomMenu = React.forwardRef(
        ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
            const [value, setValue] = useState('');

            return (
                <div
                    ref={ref}
                    style={style}
                    className={className}
                    aria-labelledby={labeledBy}
                >
                    <FormControl
                        autoFocus
                        className="mx-3 my-2 w-auto"
                        placeholder="Type to filter..."
                        onChange={(e) => setValue(e.target.value)}
                        value={value}
                    />
                    <ul className="list-unstyled">
                        {React.Children.toArray(children).filter(
                            (child) =>
                                !value || child.props.children.toLowerCase().startsWith(value),
                        )}
                    </ul>
                </div>
            );
        },
    );

    render() {
        const { viewport } = this.state;
        return (
            <div className="App">
                <p>The Current Information for UK - {this.state.information["Date"]}</p>
                <p>Active Cases: {this.state.information["Active"]}</p>
                <p>Confirmed Cases: {this.state.information["Confirmed"]}</p>
                <p>Current Deaths: {this.state.information["Deaths"]}</p>

                <Dropdown>
                    <Dropdown.Toggle as={this.CustomToggle} id="dropdown-custom-components">
                        Custom toggle
                    </Dropdown.Toggle>

                    <Dropdown.Menu as={this.CustomMenu}>
                        <Dropdown.Item eventKey="1">Red</Dropdown.Item>
                        <Dropdown.Item eventKey="2">Blue</Dropdown.Item>
                        <Dropdown.Item eventKey="3" active>
                            Orange
                        </Dropdown.Item>
                        <Dropdown.Item eventKey="1">Red-Orange</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>,

            </div>
        );
    }

}

export default App;
