import React, { Component } from 'react';
import './App.css';
import {BrowserRouter, Switch, Route} from "react-router-dom";
import ReactMapGL from 'react-map-gl';

mapboxgl.accessToken = "pk.eyJ1IjoiZWxpamFoYWhtYWQiLCJhIjoiY2tnZ2tqdDkyMDI5bDMwcGdhcDAxNXFyNiJ9.3sIoybXDm1A_gILEb1b9CA";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            information: {},
            viewport: {
                width: 400,
                height: 400,
                latitude: 37.7577,
                longitude: -122.4376,
                zoom: 8
            }
        }
    }

    componentWillMount() {
        fetch('/yesterday/uk').then(res => res.json()).then(data => {
            this.setState({information: data[0]});
            console.log(this.state.information);
        });

        const map = new mapboxgl.Map({
            container: this.state.mapContainerRef.current,
            // See style options here: https://docs.mapbox.com/api/maps/#styles
            style: 'mapbox://styles/mapbox/streets-v11',
            center: [-104.9876, 39.7405],
            zoom: 12.5,
        });

        // add navigation control (the +/- zoom buttons)
        map.addControl(new mapboxgl.NavigationControl(), 'bottom-right');

        // clean up on unmount
        return () => map.remove();
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <div className="map-container" ref={this.state.mapContainerRef} />

                    <p>The Current Information for UK - {this.state.information["Date"]}</p>
                    <p>Active Cases: {this.state.information["Active"]}</p>
                    <p>Confirmed Cases: {this.state.information["Confirmed"]}</p>
                    <p>Current Deaths: {this.state.information["Deaths"]}</p>
                </header>
            </div>
        );
    }

}

export default App;
