import React, { Component } from 'react';
import './App.css';
import {BrowserRouter, Switch, Route} from "react-router-dom";
import ReactMapGL from 'react-map-gl';

mapboxgl.accessToken = "pk.eyJ1IjoiZWxpamFoYWhtYWQiLCJhIjoiY2tnZ2tqdDkyMDI5bDMwcGdhcDAxNXFyNiJ9.3sIoybXDm1A_gILEb1b9CA";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            information: {},
            viewport: {
                width: 400,
                height: 400,
                latitude: 37.7577,
                longitude: -122.4376,
                zoom: 8
            }
        }
    }

    componentWillMount() {
        fetch('/yesterday/uk').then(res => res.json()).then(data => {
            this.setState({information: data[0]});
            console.log(this.state.information);
        });
    }

    render() {
        const { viewport } = this.state;
        return (
            <div className="App">
                <header className="App-header">

                    <p>The Current Information for UK - {this.state.information["Date"]}</p>
                    <p>Active Cases: {this.state.information["Active"]}</p>
                    <p>Confirmed Cases: {this.state.information["Confirmed"]}</p>
                    <p>Current Deaths: {this.state.information["Deaths"]}</p>
                    <ReactMapGL
                        width={viewport.width}
                        height={viewport.height}
                        latitude={viewport.latitude}
                        longitude={viewport.longitude}
                        zoom={viewport.zoom}

                        onViewportChange={(viewport) => this.setState({viewport})}
                    />
                </header>
            </div>
        );
    }

}

export default App;
